import React, { useState } from 'react';
import StateSelector from './components/StateSelector';
import WeatherDisplay from './components/WeatherDisplay';
import './styles.css';

const App = () => {
  const [weatherData, setWeatherData] = useState(null);

  const handleStateChange = async (state) => {
    try {
      const response = await fetch(`/api/weather/${state}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Check if the response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('Expected JSON response, got something else');
      }

      const data = await response.json();
      setWeatherData(data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setWeatherData(null); // Clear the weather data on error
    }
  };

  return (
    <div className="App">
      <StateSelector onStateChange={handleStateChange} />
      {weatherData ? (
        <WeatherDisplay data={weatherData} />
      ) : (
        <p>Select a state to see the weather information.</p>
      )}
    </div>
  );
};

export default App;
