import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StateSelector = ({ onSelectState }) => {
  const [states, setStates] = useState([]);

  useEffect(() => {
    // Fetch states list from an API or use a static list
    setStates(['State1', 'State2', 'State3', /* ... */ 'State30']);
  }, []);

  const handleChange = async (e) => {
    const state = e.target.value;
    // Fetch weather for selected state
    const response = await axios.get(`/api/weather?state=${state}`);
    onSelectState(response.data);
  };

  return (
    <div className="state-select">
      <label htmlFor="state">Select State:</label>
      <select id="state" onChange={handleChange}>
        <option value="">-- Select --</option>
        {states.map((state, index) => (
          <option key={index} value={state}>{state}</option>
        ))}
      </select>
    </div>
  );
};

export default StateSelector;
