import React, { useState } from 'react';
import WeatherDisplay from './WeatherDisplay';
import StateSelector from './StateSelector';
import './styles.css';

const App = () => {
  const [weather, setWeather] = useState({ temperature: '', rainProbability: '' });

  const handleStateSelect = (weatherData) => {
    setWeather(weatherData);
  };

  return (
    <div className="app-container">
      <WeatherDisplay temperature={weather.temperature} rainProbability={weather.rainProbability} />
      <StateSelector onSelectState={handleStateSelect} />
    </div>
  );
};

export default App;
