import React, { useState } from 'react';
import WeatherDisplay from './components/WeatherDisplay';
import StateSelector from './components/StateSelector';
import './styles.css';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [weather, setWeather] = useState({ temperature: '', chanceOfRain: '' });

  const handleStateChange = (state) => {
    setSelectedState(state);
    // Fetch weather data based on the selected state
    // Update weather state
  };

  return (
    <div className="app-container">
      <video autoPlay loop muted>
        <source src="./white.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <StateSelector selectedState={selectedState} onStateChange={handleStateChange} style={{ marginTop: '20px', padding: '10px' }} />
      <WeatherDisplay
        temperature={weather.temperature}
        chanceOfRain={weather.chanceOfRain}
        style={{ textAlign: 'center', padding: '20px', background: 'rgba(0, 0, 0, 0.5)', borderRadius: '10px' }}
      />
    </div>
  );
};

export default App;
