import React, { useState, useEffect } from 'react';
import WeatherDisplay from './components/WeatherDisplay';
import StateSelector from './components/StateSelector';
import "./styles.css";
import axios from 'axios';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [weather, setWeather] = useState({ temperature: '', chanceOfRain: '' });

  useEffect(() => {
    if (selectedState) {
      axios.get(`http://localhost:5000/api/weather/${selectedState}`)
        .then(response => {
          setWeather({
            temperature: response.data.temperature,
            chanceOfRain: response.data.chanceOfRain
          });
        })
        .catch(error => {
          console.error('Error fetching weather data:', error);
        });
    }
  }, [selectedState]);

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
  };

  return (
    <div className="app-container">
      <StateSelector selectedState={selectedState} onStateChange={handleStateChange} />
      <WeatherDisplay
        temperature={weather.temperature}
        chanceOfRain={weather.chanceOfRain}
      />
    </div>
  );
};

export default App;
