import React, { useState } from 'react';
import WeatherDisplay from './components/WeatherDisplay';
import StateSelector from './components/StateSelector';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [weather, setWeather] = useState({ temperature: '', chanceOfRain: '' });

  const handleStateChange = (state) => {
    setSelectedState(state);
    // Fetch weather data based on the selected state
    // Update weather state
  };

  const containerStyle = {
    backgroundImage: 'url(./weather2.jpg)', // Ensure correct path
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    textAlign: 'center',
  };

  const weatherInfoStyle = {
    padding: '20px',
    background: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background
    borderRadius: '10px',
    marginTop: '20px',
  };

  const stateSelectStyle = {
    marginTop: '20px',
    padding: '10px',
  };

  return (
    <div style={containerStyle}>
      <StateSelector selectedState={selectedState} onStateChange={handleStateChange} style={stateSelectStyle} />
      <WeatherDisplay
        temperature={weather.temperature}
        chanceOfRain={weather.chanceOfRain}
        style={weatherInfoStyle}
      />
    </div>
  );
};

export default App;
