import React, { useState } from 'react';
import WeatherDisplay from './components/WeatherDisplay';
import StateSelector from './components/StateSelector';
import axios from 'axios';
import './styles.css';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [weather, setWeather] = useState({ temperature: '', chanceOfRain: '' });

  const handleStateChange = async (state) => {
    setSelectedState(state);
    
    try {
      const response = await axios.get(`/api/weather/${state}`);
      const { temp_c, precip_mm } = response.data.current; // Adjust based on your API response

      setWeather({
        temperature: temp_c,
        chanceOfRain: precip_mm
      });
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  return (
    <div className="app-container">
      <StateSelector selectedState={selectedState} onStateChange={handleStateChange} />
      <WeatherDisplay
        temperature={weather.temperature}
        chanceOfRain={weather.chanceOfRain}
      />
    </div>
  );
};

export default App;
