import React, { useState } from 'react';
import StateSelector from './components/StateSelector';
import WeatherDisplay from './components/WeatherDisplay';
import './styles.css';

const App = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleStateChange = async (state) => {
    try {
      // Clear previous error messages and weather data
      setErrorMessage(null);
      setWeatherData(null);

      // Fetch weather data for the selected state
      const response = await fetch(`/api/weather/${state}`);
      if (!response.ok) {
        throw new Error(`Network response was not ok: ${response.statusText}`);
      }

      // Check that the response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('Expected JSON response, got something else');
      }

      // Parse the JSON response
      const data = await response.json();
      setWeatherData(data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setErrorMessage(`Failed to fetch weather data. Please try again later. Details: ${error.message}`);
    }
  };

  return (
    <div className="App">
      <StateSelector onStateChange={handleStateChange} />
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {weatherData ? (
        <WeatherDisplay data={weatherData} />
      ) : (
        !errorMessage && <p>Select a state to see the weather information.</p>
      )}
    </div>
  );
};

export default App;
